import React, { useState, useEffect } from "react";
import contentData from "./data/content";
import CategoryMenu from "./components/CategoryMenu";
import Carousel from "./components/Carousel";
import AddItemForm from "./components/AddItemForm";

function App() {
  const savedCategory = localStorage.getItem("selected-category") || "Сериалы";
  const savedPages = JSON.parse(localStorage.getItem("selected-pages") || "{}");

  const [category, setCategory] = useState(savedCategory);
  const [pagesByCategory, setPagesByCategory] = useState(savedPages);
  const currentPage = pagesByCategory[category] || 0;

  const [content, setContent] = useState(() => {
    const stored = localStorage.getItem("what-to-watch-content");
    return stored ? JSON.parse(stored) : contentData;
  });

  const [isAdding, setIsAdding] = useState(false);

  useEffect(() => {
    localStorage.setItem("what-to-watch-content", JSON.stringify(content));
  }, [content]);

  useEffect(() => {
    localStorage.setItem("selected-category", category);
    localStorage.setItem("selected-pages", JSON.stringify(pagesByCategory));
  }, [category, pagesByCategory]);

  const updateItem = (categoryName, index, updates) => {
    const updatedCategory = [...content[categoryName]];
    updatedCategory[index] = { ...updatedCategory[index], ...updates };
    setContent((prev) => ({ ...prev, [categoryName]: updatedCategory }));
  };

  const deleteItem = (categoryName, index) => {
    const confirmed = window.confirm("Вы уверены, что хотите скрыть эту позицию?");
    if (!confirmed) return;
    const updatedCategory = [...content[categoryName]];
    updatedCategory[index].hidden = true;
    setContent((prev) => ({ ...prev, [categoryName]: updatedCategory }));
  };

  const addItem = (categoryName, newItem) => {
    const updatedCategory = [newItem, ...(content[categoryName] || [])];
    setContent((prev) => ({ ...prev, [categoryName]: updatedCategory }));
    setCategory(categoryName);
    setPagesByCategory((prev) => ({ ...prev, [categoryName]: 0 }));
    setIsAdding(false);
  };

  const allItems = content[category] || [];
  const visibleItems = allItems
    .map((item, i) => ({ ...item, originalIndex: i }))
    .filter((item) => !item.hidden);

  const sortedItems = [...visibleItems].sort((a, b) => {
    if (a.watched && !b.watched) return 1;
    if (!a.watched && b.watched) return -1;
    return 0;
  });

  return (
    <div className="container">
      <div className="header-container">
        <span className="header">Что посмотреть?</span>
        <CategoryMenu
          selected={category}
          onSelect={(cat) => {
            setCategory(cat);
            setPagesByCategory((prev) => ({ ...prev, [category]: currentPage }));
          }}
        />
        <button className="add-button" onClick={() => setIsAdding(true)}>+</button>
      </div>

      {isAdding ? (
        <AddItemForm onAdd={addItem} onCancel={() => setIsAdding(false)} />
      ) : (
        <Carousel
          items={sortedItems}
          currentPage={currentPage}
          onPageChange={(newPage) => {
            setPagesByCategory((prev) => ({ ...prev, [category]: newPage }));
          }}
          onRate={(index, stars) => updateItem(category, sortedItems[index].originalIndex, { rating: stars })}
          onToggleWatched={(index) => {
            const originalIndex = sortedItems[index].originalIndex;
            const current = allItems[originalIndex].watched;
            updateItem(category, originalIndex, { watched: !current });
          }}
          onDelete={(index) => deleteItem(category, sortedItems[index].originalIndex)}
        />
      )}
    </div>
  );
}

export default App;
